pragma solidity ^0.5.0;

import "@openzeppelin/contracts/GSN/Context.sol";
import "@openzeppelin/contracts/math/Math.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract CrowdsaleByUSDT is Context, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // 用于众筹的代币地址
    IERC20 public usdtToken;
    // 兑换比例，每1个输入资产兑换多少代币数量
    uint256 public rate;
    // 接收资金的账户地址
    address payable public wallet;
    // 兑换代币地址
    IERC20 public token;
    // 兑换代币发出地址
    address public tokenWallet;
    // 已兑换额度
    uint256 public weiRaised;
    // 每个地址已众筹的额度
    mapping(address => uint256) public joined;
    // 已众筹份额
    uint256 public soldNumber = 0;
    // 总众筹份额
    uint256 public totalNumber = 1000;
    // 众筹最小认购金额
    uint256 public minCrowdsaleAmount = 150;
    // 众筹最大认购金额
    uint256 public maxCrowdsaleAmount = 1500;

    /**
     * Event for token purchase logging
     * @param purchaser who paid for the tokens
     * @param beneficiary who got the tokens
     * @param value weis paid for purchase
     * @param amount amount of tokens purchased
     */
    event TokensPurchased(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);

    constructor(
        IERC20 _usdtToken,   // 用于众筹的代币地址
        uint256 _rate,           // 兑换比例
        address payable _wallet, // 接收ETH受益人地址
        IERC20 _token,           // 代币地址
        address _tokenWallet     // 代币从这个地址发送
    ) public {
        require(address(_usdtToken) != address(0), "Crowdsale: purchase token is the zero address");
        require(_rate > 0, "Crowdsale: rate is 0");
        require(_wallet != address(0), "Crowdsale: wallet is the zero address");
        require(address(_token) != address(0), "Crowdsale: token is the zero address");
        require(_tokenWallet != address(0), "Crowdsale: token wallet is the zero address");

        usdtToken = _usdtToken;
        rate = _rate;
        wallet = _wallet;
        token = _token;
        tokenWallet = _tokenWallet;
    }

    // 向合约转账
    function () external payable {
        wallet.transfer(msg.value);
    }

    // 进行众筹
    function buyTokens(uint256 weiAmount) public nonReentrant payable {
        _checkAmount(msg.sender, weiAmount);
        _buyTokens(msg.sender, weiAmount);
        // 接收众筹
        usdtToken.safeTransferFrom(msg.sender, wallet, weiAmount);
    }

    // 校验金额
    function _checkAmount(address beneficiary, uint256 amount) internal view {
        // 同一个地址只能参与一次众筹
        require(joined[beneficiary] == 0, 'Crowdsale: The address has participated in crowdfunding');
        // 众筹份额不能超过1000份
        require(soldNumber < totalNumber, 'Crowdsale: crowdfunding quota sold out');
        uint256 amountToNormal = amount / (10**uint256(18));
        // 众筹金额范围
        require(amountToNormal >= minCrowdsaleAmount && amountToNormal <= maxCrowdsaleAmount, 'Crowdsale: the amount ranges from 150 to 1500 usdt');
        this;
    }

    // 执行众筹
    function _buyTokens(address beneficiary, uint256 weiAmount) internal {
        // 计算需要转出的token数量
        uint256 tokens = weiAmount.mul(rate);
        // 更新销售额
        weiRaised = weiRaised.add(weiAmount);
        // 代币转账
        token.safeTransferFrom(tokenWallet, beneficiary, tokens);
        emit TokensPurchased(msg.sender, beneficiary, weiAmount, tokens);
        // 记录参与人已购买金额
        joined[beneficiary] = joined[beneficiary].add(weiAmount);
        // 记录众筹份额
        soldNumber = soldNumber.add(1);
    }

    // 计算剩余众筹额度
    function remainingTokens() public view returns (uint256) {
        return Math.min(token.balanceOf(tokenWallet), token.allowance(tokenWallet, address(this)));
    }
}

